import { createGlobalStyle, css } from 'styled-components'

export default createGlobalStyle`
${({ theme }) => css`
  html {
    height: 100%;

    body {
      display: flex;
      flex-direction: column;
      height: 100%;
      margin: 0;

      #root {
        background: ${(theme as any).colors.background};
        color: ${(theme as any).colors.black};
        display: flex;
        height: 100%;
      }
    }
  }
`}
`
