import React, { useContext } from 'react'
import styled from 'styled-components'
import { FaMoon, FaSun } from 'react-icons/fa'
import { ThemeContext } from '../AppProvider'
import Switch from 'react-switch'

export const ThemeToggle: React.FC = () => {
  const { toggleTheme, themeMode } = useContext(ThemeContext as any)
  const handleThemeChange = () => {
    toggleTheme()
  }
  return (
    <Root>
      <h1>Toggle Theme</h1>
      <Switch
        checked={themeMode === 'light' ? true : false}
        className="test"
        height={50}
        width={120}
        checkedIcon={
          <FaSun
            style={{
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              height: '100%',
              fontSize: 35,
              paddingLeft: 10
            }}
            color={themeMode === 'light' ? 'white' : 'grey'}
            className="light"
          />
        }
        uncheckedIcon={
          <FaMoon
            style={{
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              height: '100%',
              fontSize: 35,
              paddingLeft: 24
            }}
            color={themeMode === 'dark' ? 'blue' : 'blue'}
            className="dark"
          />
        }
        onChange={handleThemeChange}
      />
    </Root>
  )
}

const Root = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  .custom-classname .react-toggle--checked .react-toggle-track {
    background-color: red;
    height: 200px;
    width: 200px;
    padding: 30px;
  }
`
